package game;

import java.util.Random;

import org.testng.Assert;
import org.testng.annotations.Test;

public class TestNG {

	/**
	 * This method is used to handle each move that the testing suite wants to make.
	 * @param move The move that is going to be made
	 */
	public void CompleteMove(Move move, Game game) {
		//System.out.print(move);
		if (move.IsValid() && game.rules.ValidateMove(move, game.board) == 0) {
			MoveCompleteResult moveResult = game.CompleteMove(move);
			game.NextTurn();
			//System.out.println(move.piece);
			//new View().DisplayBoard(game.board);
			// check for game ending condition
			if (moveResult.gameOver) {
				game.GameOver = true;
			}
		}

	}

	@Test
	public void testGameInstantiation() {

		Game game = new Game();

		Assert.assertNotNull(game.white);
		Assert.assertNotNull(game.black);
		Assert.assertEquals(game.turnNumber, 0);
		Assert.assertEquals(game.activePlayer, game.black);
	}

	@Test
	public void testBoardInstantiation() {

		Board board = new Board(8,8);

		Assert.assertEquals(board.width, 8);
		Assert.assertEquals(board.height, 8);
		Assert.assertNotNull(board.tiles);

		Board newBoard = new Board(board);

		Assert.assertEquals(newBoard.width, 8);
		Assert.assertEquals(newBoard.height, 8);
		Assert.assertNotNull(newBoard.tiles);
	}

	@Test
	public void testPieceInstantiation() {

		Player white = new Player();
		white.color = "White";
		Piece piece = new King(white);

		Assert.assertEquals(white, piece.owner);
		Assert.assertEquals("K", piece.toString());
	}

	@Test
	public void testAddPiece() {
		Board board = new Board(8,8);
		Player white = new Player();
		white.color = "White";

		Piece piece = new King(white);
		board.AddPiece(piece, 3, 3);

		Assert.assertEquals(board.tiles[3][3].piece, piece);
	}

	@Test
	public void testCheck() {
		View view = new View();
		Game game = new Game();
		game.rules = new ClassicChess();
		game.board = new Board(8,8);
		game.board.AddPiece(new King(game.white), 2, 2);
		game.board.AddPiece(new Knight(game.white), 3, 3);
		game.board.AddPiece(new Knight(game.black), 5, 5);
		game.board.AddPiece(new King(game.black), 6, 6);
		game.NextTurn();
		CompleteMove(new Move(game.activePlayer, "d3-e5", game.board), game);

		Assert.assertEquals(game.rules.CheckForCheck(game.activePlayer.opponent, game.board), true);
		Assert.assertEquals(game.GameOver, false);
	}

	@Test
	public void testCheckMate() {
		View view = new View();
		Game game = new Game();
		game.rules = new ClassicChess();
		game.board = new Board(8,8);
		game.board.AddPiece(new King(game.white), 2, 2);
		game.board.AddPiece(new Rook(game.white), 7, 2);
		game.board.AddPiece(new Rook(game.white), 0, 4);
		game.board.AddPiece(new Rook(game.white), 0, 6);
		game.board.AddPiece(new King(game.black), 5, 5);
		game.NextTurn();
		CompleteMove(new Move(game.activePlayer, "h2-h5", game.board), game);

		Assert.assertEquals(game.rules.CheckForCheck(game.activePlayer.opponent, game.board), true);
		Assert.assertEquals(game.GameOver, true);
	}

	@Test
	public void testFuzzInput() {
		View view = new View();
		Game game = new Game();
		game.rules = new ClassicChess();
		game.board = game.rules.SetStartingPositions(game.white, game.black);

		game.NextTurn();

		int i = 0;
		while (i < 10000) {
			CompleteMove(genRandomMove(game.activePlayer, game.board), game);
			i++;
		}

	}

	//generates a random, not necessarily valid move
	private Move genRandomMove(Player player, Board board) {
		int min = 0;
		int max = board.listOfTiles.size() - 1;

		Tile startTile = board.listOfTiles.get(new Random().nextInt((max - min) + 1) + min);
		Tile endTile = board.listOfTiles.get(new Random().nextInt((max - min) + 1) + min);

		return new Move(player, startTile, endTile);
	}
}

package game;

import java.io.BufferedReader;
import java.io.IOException;
import java.io.InputStreamReader;

/**
 * Take all chess variant rules class.
 */
public class TakeAllChess extends Rules {

	public TakeAllChess() {
		super();
	}

	/**
	 * @see game.Rules#SetStartingPositions(game.Player, game.Player)
	 */
	public Board SetStartingPositions(Player white, Player black) {

		Board board = new Board(8,8);

		board.AddPiece(new Pawn(white), 0, 1);
		board.AddPiece(new Pawn(white), 1, 1);
		board.AddPiece(new Pawn(white), 2, 1);
		board.AddPiece(new Pawn(white), 3, 1);
		board.AddPiece(new Pawn(white), 4, 1);
		board.AddPiece(new Pawn(white), 5, 1);
		board.AddPiece(new Pawn(white), 6, 1);
		board.AddPiece(new Pawn(white), 7, 1);
		board.AddPiece(new Rook(white), 0, 0);
		board.AddPiece(new Knight(white), 1, 0);
		board.AddPiece(new Bishop(white), 2, 0);
		board.AddPiece(new Queen(white), 3, 0);
		board.AddPiece(new King(white), 4, 0);
		board.AddPiece(new Bishop(white), 5, 0);
		board.AddPiece(new Knight(white), 6, 0);
		board.AddPiece(new Rook(white), 7, 0);

		board.AddPiece(new Pawn(black), 0, 6);
		board.AddPiece(new Pawn(black), 1, 6);
		board.AddPiece(new Pawn(black), 2, 6);
		board.AddPiece(new Pawn(black), 3, 6);
		board.AddPiece(new Pawn(black), 4, 6);
		board.AddPiece(new Pawn(black), 5, 6);
		board.AddPiece(new Pawn(black), 6, 6);
		board.AddPiece(new Pawn(black), 7, 6);
		board.AddPiece(new Rook(black), 0, 7);
		board.AddPiece(new Knight(black), 1, 7);
		board.AddPiece(new Bishop(black), 2, 7);
		board.AddPiece(new Queen(black), 3, 7);
		board.AddPiece(new King(black), 4, 7);
		board.AddPiece(new Bishop(black), 5, 7);
		board.AddPiece(new Knight(black), 6, 7);
		board.AddPiece(new Rook(black), 7, 7);

		return board;
	}


	/**
	 * @return true if it's a valid move, false if invalid
	 */
	public boolean validatePawnMove(Move move, Board board)
	{
		// get direction it can go in
		int direction = -1;

		// white on top
		if(move.activePlayer.color.equals("White")) {
			direction = 1;
		}

		int yDifference = move.endPosition.yCord - move.startPosition.yCord;
		int xDifference = Math.abs(move.endPosition.xCord - move.startPosition.xCord);
		if(yDifference == direction) {
			// pawn is moving straight by 1 space
			if(xDifference == 0) {
				// if there is no piece ahead, it's valid
				if(move.endPosition.piece == null) {
					return true;
				}
				else {
					return false;
				}
			}
			// pawn is moving diagonal by 1 space
			else if(xDifference == 1) {
				// can't move diagonal without a piece
				if(move.endPosition.piece == null) {
					//checking if the piece adjacent to the pawn moving was a pawn and had done a double jump the last turn (checking condition for En Passant)
					if(board.tiles[move.endPosition.xCord][move.startPosition.yCord].piece != null){
						if(board.tiles[move.endPosition.xCord][move.startPosition.yCord].piece.getClass() == Pawn.class){
							Pawn p = (Pawn)board.tiles[move.endPosition.xCord][move.startPosition.yCord].piece;
							if(p.lastMoveJump == true && p.owner != move.activePlayer){
								board.tiles[move.endPosition.xCord][move.startPosition.yCord].piece = null;
								return true;
							}
						}
					}
					return false;
				}
				// can only move diagonal if the piece is owned by the other player
				else if(move.endPosition.piece.owner != move.activePlayer) {
					return true;
				}
			}
		}
		else if(move.piece.hasMoved == false && yDifference == direction * 2 && xDifference == 0) {
			// double jump for first move
			//checking that the final position is empty (does not contain a piece) and that the space in between is also empty.
			if(move.endPosition.piece == null && board.tiles[move.startPosition.xCord][move.startPosition.yCord + direction].piece == null) {
				return true;
			}
			else {
				return false;
			}
		}
		return false;
	}

	/**
	 * @return true if it's a valid move, false if invalid
	 */
	public boolean validateQueenMove(Move move, Board board)
	{
		// check what the move way is, horizontal or diagonal
		int xDiff = Math.abs(move.startPosition.xCord - move.endPosition.xCord);
		int yDiff = Math.abs(move.startPosition.yCord - move.endPosition.yCord);

		// if it attacks the same team
		if(move.endPosition.piece != null && move.endPosition.piece.owner == move.activePlayer) {
			return false;
		}

		int xDir = 1;
		int yDir = 1;

		// moving left
		if(move.startPosition.xCord - move.endPosition.xCord > 0) {
			xDir = -1;
		}
		// moving up
		if(move.startPosition.yCord - move.endPosition.yCord > 0) {
			yDir = -1;
		}

		// diagonal movement
		if(xDiff == yDiff) {
			for(int i=1; i < yDiff; i++ ) {
				Tile t = board.tiles[move.startPosition.xCord + (xDir * i)][move.startPosition.yCord + (yDir * i)];
				if(t.piece != null) {
					return false;
				}
			}
			return true;
		}
		// horizontal movement
		else if(yDiff == 0) {
			for(int i=1; i < xDiff; i++ ) {
				Tile t = board.tiles[move.startPosition.xCord + (xDir * i)][move.startPosition.yCord];
				if(t.piece != null) {
					return false;
				}
			}
			return true;
		}
		// vertical movement
		else if(xDiff == 0) {
			for(int i=1; i < yDiff; i++ ) {
				Tile t = board.tiles[move.startPosition.xCord][move.startPosition.yCord + (yDir * i)];
				if(t.piece != null) {
					return false;
				}
			}
			return true;
		}

		return false;
	}

	/**
	 * @return true if it's a valid move, false if invalid
	 */
	public boolean validateBishopMove(Move move, Board board)
	{
		// check what the move way is, horizontal or diagonal
		int xDiff = Math.abs(move.startPosition.xCord - move.endPosition.xCord);
		int yDiff = Math.abs(move.startPosition.yCord - move.endPosition.yCord);

		// if it attacks the same team
		if(move.endPosition.piece != null && move.endPosition.piece.owner == move.activePlayer) {
			return false;
		}

		// diagonal movement
		if(xDiff == yDiff) {
			int xDir = 1;
			int yDir = 1;

			// moving left
			if(move.startPosition.xCord - move.endPosition.xCord > 0) {
				xDir = -1;
			}
			// moving up
			if(move.startPosition.yCord - move.endPosition.yCord > 0) {
				yDir = -1;
			}

			for(int i=1; i < yDiff; i++ ) {
				Tile t = board.tiles[move.startPosition.xCord + (xDir * i)][move.startPosition.yCord + (yDir * i)];
				if(t.piece != null) {
					return false;
				}
			}
			return true;
		}

		return false;
	}

	/** 
	 * @return true if it's a valid move, false if invalid
	 */
	public boolean validateRookMove(Move move, Board board)
	{
		// check what the move way is, horizontal or diagonal
		int xDiff = Math.abs(move.startPosition.xCord - move.endPosition.xCord);
		int yDiff = Math.abs(move.startPosition.yCord - move.endPosition.yCord);

		// if it attacks the same team
		if(move.endPosition.piece != null && move.endPosition.piece.owner == move.activePlayer) {
			return false;
		}

		int xDir = 1;
		int yDir = 1;

		// moving left
		if(move.startPosition.xCord - move.endPosition.xCord > 0) {
			xDir = -1;
		}
		// moving up
		if(move.startPosition.yCord - move.endPosition.yCord > 0) {
			yDir = -1;
		}

		// vertical movement
		if(xDiff == 0) {
			for(int i=1; i < yDiff; i++ ) {

				Tile t = board.tiles[move.startPosition.xCord][move.startPosition.yCord + (yDir * i)];
				if(t.piece != null) {
					return false;
				}
			}
			return true;
		}
		// horizontal movement
		else if(yDiff == 0) {
			for(int i=1; i < xDiff; i++ ) {
				Tile t = board.tiles[move.startPosition.xCord + (xDir * i)][move.startPosition.yCord];
				if(t.piece != null) {
					return false;
				}
			}
			return true;
		}

		return false;
	}

	/** 
	 * @return true if it's a valid move, false if invalid
	 */
	public boolean validateKnightMove(Move move, Board board)
	{
		// if it attacks the same team
		if(move.endPosition.piece != null && move.endPosition.piece.owner == move.activePlayer) {
			return false;
		}

		// check what the move way is, horizontal or diagonal
		int xDiff = Math.abs(move.startPosition.xCord - move.endPosition.xCord);
		int yDiff = Math.abs(move.startPosition.yCord - move.endPosition.yCord);


		if(yDiff == 2 && xDiff == 1) {
			return true;
		}
		else if(yDiff == 1 && xDiff == 2) {
			return true;
		}

		return false;
	}

	/** 
	 * @return true if it's a valid move, false if invalid
	 */
	public boolean validateKingMove(Move move, Board board)
	{
		// if it attacks the same team
		if(move.endPosition.piece != null && move.endPosition.piece.owner == move.activePlayer) {
			return false;
		}

		// check what the move way is, horizontal or diagonal
		int xDiff = Math.abs(move.startPosition.xCord - move.endPosition.xCord);
		int yDiff = Math.abs(move.startPosition.yCord - move.endPosition.yCord);

		if(yDiff <= 1 && xDiff <= 1) {
			return true;
		}

		return false;
	}


	/**
	 * 
	 * @see game.Rules#ValidateMove(game.Move, game.Board)
	 */
	public int ValidateMove(Move move, Board board)
	{
		int isValidMove = -1;

		if(move.piece.getClass() == Pawn.class) {
			isValidMove = (validatePawnMove(move, board) == true) ? 0 : 1;
		}
		else if(move.piece.getClass() == Bishop.class) {
			isValidMove = (validateBishopMove(move, board) == true) ? 0 : 1;
		}
		else if(move.piece.getClass() == Queen.class) {
			isValidMove = (validateQueenMove(move, board) == true) ? 0 : 1;
		}
		else if(move.piece.getClass() == Rook.class) {
			isValidMove = (validateRookMove(move, board) == true) ? 0 : 1;
		}
		else if(move.piece.getClass() == Knight.class) {
			isValidMove = (validateKnightMove(move, board) == true) ? 0 : 1;
		}
		else if(move.piece.getClass() == King.class) {
			isValidMove = (validateKingMove(move, board) == true) ? 0 : 1;
		}

		if(isValidMove == 1) {
			View.setErrorMessage("Illegally inputted move, piece can't move in the specified manner.");
			return 1;
		}

		if(move.endPosition.piece != null && move.endPosition.piece.owner != move.activePlayer) { //Player is killing the opponents piece, no need to check if they have a potential kill move
			return 0;
		}

		if(checkIfKillIsPossible(move.activePlayer, board) == true) {
			return 3;
		}
		//isValidMove = validateBoardState(move, board) ? isValidMove : false;

		return 0;
		//return isValidMove;
	}

	//Pretty sure this function sets last jumped to 0 for everything, has to do with En Passant
	public void setLastMoveJump(Player activePlayer, Board board){
		for(int y = 0; y < board.height; y++) {
			for(int x = 0; x < board.width; x++) {
				if (board.tiles[x][y].piece != null) {
					if(board.tiles[x][y].piece.getClass() == Pawn.class && board.tiles[x][y].piece.owner == activePlayer){
						Pawn p = (Pawn)board.tiles[x][y].piece;
						p.lastMoveJump = false;
					}
				}
			}
		}
	}

	/**
	 * Move has been validated, performing the move
	 * @param board
	 * @param move
	 * @return 	MoveCompleteResult
	 */
	public MoveCompleteResult ruleCompleteMove(Board board, Move move){
		setLastMoveJump(move.activePlayer, board);
		if(move.piece.getClass() == Pawn.class && Math.abs(move.startPosition.yCord - move.endPosition.yCord) == 2) {
			Pawn p = (Pawn)move.piece;
			p.lastMoveJump = true;
		}else if(move.piece.getClass() == Pawn.class && move.endPosition.yCord == 7 && move.piece.owner.color.equals("White")) {
			getPawnPromotionInput(board, move);
		}else if(move.piece.getClass() == Pawn.class && move.endPosition.yCord == 0 && move.piece.owner.color.equals("Black")) {
			getPawnPromotionInput(board, move);
		}

		boolean victoryStatus = checkWinCondition(move.activePlayer, board);
		if(victoryStatus) {
			MoveCompleteResult moveResult = new MoveCompleteResult(true);
			moveResult.winner = move.activePlayer;
			return moveResult;
		}

		return new MoveCompleteResult(false);
	}

	/**
	 * Gets input for doing the Pawn Promotion
	 */
	public void getPawnPromotionInput(Board board, Move move){
		System.out.println("Please enter a valid input for Pawn Promotion. Choose between Q (Queen), R (Rook), N (Knight), and B (Bishop).");
		BufferedReader br = new BufferedReader(new InputStreamReader(System.in));
		String input = null;
		while (input == null) {
			try {
				input = br.readLine();
			} catch (IOException e) {
				input = null;
				System.out.println("Input Error: Unable to read input.");
			}
			if (input != null) {
				try {
					if(input.equalsIgnoreCase("k") || input.equalsIgnoreCase("king")) {
						board.tiles[move.endPosition.xCord][move.endPosition.yCord].addPiece(new King(move.activePlayer));
					}
					else if(input.equalsIgnoreCase("q") || input.equalsIgnoreCase("queen")) {
						board.tiles[move.endPosition.xCord][move.endPosition.yCord].addPiece(new Queen(move.activePlayer));
					}
					else if(input.equalsIgnoreCase("r") || input.equalsIgnoreCase("rook")) {
						board.tiles[move.endPosition.xCord][move.endPosition.yCord].addPiece(new Rook(move.activePlayer));
					}
					else if(input.equalsIgnoreCase("n") || input.equalsIgnoreCase("knight")) {
						board.tiles[move.endPosition.xCord][move.endPosition.yCord].addPiece(new Knight(move.activePlayer));
					}
					else if(input.equalsIgnoreCase("b") || input.equalsIgnoreCase("bishop")) {
						board.tiles[move.endPosition.xCord][move.endPosition.yCord].addPiece(new Bishop(move.activePlayer));
					}
					else{
						input = null;
						System.out.println("Please choose a valid input between K (King), Q (Queen), R (Rook), N (Knight), and B (Bishop).");
					}
				}
				catch (Exception e)
				{
					input = null;
					System.out.println("Please choose a valid input between K (King), Q (Queen), R (Rook), N (Knight), and B (Bishop).");
				}
			}
		}
	}

	/**
	 * @return false - no one has won, true - someone wins
	 * should be called after a move has happened, checks if the active player has won (all the opponents pieces are gone)
	 */
	public boolean checkWinCondition(Player activePlayer, Board board) {
		for (Tile tile : board.listOfTiles) {
			if (tile.piece != null) {
				if (tile.piece.owner != activePlayer) {
					return false;
				}
			}
		}

		return true;
	}

	/**
	 * Ran after every move is validated if the player isn't killing someone. 
	 * Returns true if that player could have killed someone but didn't
	 * Returns false otherwise
	 */
	private boolean checkIfKillIsPossible(Player activePlayer, Board board) {

		//iterate through the enemy pieces and tell them to try and kill the friendly king
		//if they can (legally) then the active player has put themself in check
		for (Tile tile : board.listOfTiles) {
			if (tile.piece != null) {
				if (tile.piece.owner == activePlayer) {
					//try to make a kill
					Move exMove = new Move();
					exMove.activePlayer = tile.piece.owner;
					exMove.startPosition = tile;
					exMove.piece = tile.piece;

					for (Tile endTile : board.listOfTiles) {
						if (endTile.piece != null) {
							if (endTile.piece.owner != activePlayer) {

								exMove.endPosition = endTile;
								boolean isValid = false;
								if(exMove.piece.getClass() == Pawn.class) {
									isValid = validatePawnMove(exMove, board);
								}
								else if(exMove.piece.getClass() == Bishop.class) {
									isValid = validateBishopMove(exMove, board);
								}
								else if(exMove.piece.getClass() == Queen.class) {
									isValid = validateQueenMove(exMove, board);
								}
								else if(exMove.piece.getClass() == Rook.class) {
									isValid = validateRookMove(exMove, board);
								}
								else if(exMove.piece.getClass() == Knight.class) {
									isValid = validateKnightMove(exMove, board);
								}
								else if(exMove.piece.getClass() == King.class) {
									isValid = validateKingMove(exMove, board);
								}
								if (isValid) {
									View.setErrorMessage("A capturing move is available but the move specified captures nothing. See below for details.\n" + exMove.toString());
									return true;
								}

							}
						}
					}

				}
			}
		}
		return false;
	}

	/**
	 * @param activePlayer - what player you want to check if has their opponent in check
	 * @param board - the board you want to check for check on.
	 * @return true if the active player is in check, false if they are not.
	 */
	public boolean CheckForCheck(Player activePlayer, Board board) {

		//locate the friendly and enemy kings
		Tile enemyKing = null;
		for (Tile tile: board.listOfTiles) {
			if (tile.piece != null) {
				if (tile.piece.getClass() == King.class && tile.piece.owner != activePlayer) {
					enemyKing = tile;
				}
			}
		}

		//if (kingPosition == null) { return false; }
		if (enemyKing == null) { return false; }

		//iterate through the active players pieces and tell them to try and kill the friendly king
		//if they can (legally) then the active player is in check
		for (Tile tile : board.listOfTiles) {
			if (tile.piece != null) {
				if (tile.piece.owner == activePlayer) {
					//try to kill the king
					Move regicide = new Move();
					regicide.activePlayer = tile.piece.owner;
					regicide.startPosition = tile;
					regicide.endPosition = enemyKing;
					regicide.piece = tile.piece;

					boolean isValid = false;
					if(regicide.piece.getClass() == Pawn.class) {
						isValid = validatePawnMove(regicide, board);
					}
					else if(regicide.piece.getClass() == Bishop.class) {
						isValid = validateBishopMove(regicide, board);
					}
					else if(regicide.piece.getClass() == Queen.class) {
						isValid = validateQueenMove(regicide, board);
					}
					else if(regicide.piece.getClass() == Rook.class) {
						isValid = validateRookMove(regicide, board);
					}
					else if(regicide.piece.getClass() == Knight.class) {
						isValid = validateKnightMove(regicide, board);
					}
					else if(regicide.piece.getClass() == King.class) {
						isValid = validateKingMove(regicide, board);
					}
					if (isValid) {
						return true;
					}
				}
			}
		}

		return false;
	}

	/**
	 * Called before every move, checks for stalemate and ends the game accordingly.
	 */
	public boolean checkForStalemate(Player activePlayer, Board board) {
		//Check all pieces for a valid move, to start with the player doesn't need to move his King.
		Tile kingTile = null;

		for (Tile tile : board.listOfTiles) {
			if (tile.piece != null) {
				if (tile.piece.owner == activePlayer) {
					Move exMove = new Move();
					exMove.activePlayer = tile.piece.owner;
					exMove.startPosition = tile;
					exMove.piece = tile.piece;
					if(tile.piece.getClass() == King.class) {
						kingTile = tile;
					}
					for (Tile endTile : board.listOfTiles) {
						exMove.endPosition = endTile;
						boolean isValid = false;
						if(exMove.piece.getClass() == Pawn.class) {
							isValid = validatePawnMove(exMove, board);
						}
						else if(exMove.piece.getClass() == Bishop.class) {
							isValid = validateBishopMove(exMove, board);
						}
						else if(exMove.piece.getClass() == Queen.class) {
							isValid = validateQueenMove(exMove, board);
						}
						else if(exMove.piece.getClass() == Rook.class) {
							isValid = validateRookMove(exMove, board);
						}
						else if(exMove.piece.getClass() == Knight.class) {
							isValid = validateKnightMove(exMove, board);
						}
						if (isValid) {
							return false;
						}
					}
				}
			}
		}

		//Has no valid moves other than potentially the king.
		if(kingTile == null) { //player has no king, no king and no valid moves = stalemate.
			return true;
		}

		//Loop through all the potential moved for the king, and check if the player is in check for each of them.
		Move exMove = new Move();
		exMove.activePlayer = activePlayer;
		exMove.startPosition = kingTile;
		exMove.piece = kingTile.piece;

		for (Tile endTile : board.listOfTiles) {
			exMove.endPosition = endTile;
			if(!validateKingMove(exMove, board)) {
				continue;
			}
			//is a valid move for the king. Perform the move on a fake board and check for check.
			Board newBoard = new Board(board);	
			newBoard.tiles[endTile.xCord][endTile.yCord].piece = new King(activePlayer);
			newBoard.tiles[kingTile.xCord][kingTile.yCord].piece = null;

			if(!CheckForCheck(activePlayer.opponent, newBoard)) { //opponent doesn't have them in check after the move
				return false;
			}
		}

		return true;
	}

	/**
	 * Gives the stalemate message based on which player was put into stalemate.
	 * @param activePlayer - The player who's turn it currently is.
	 * @return true if the current player was just put into stalemate, false if they weren't
	 */
	public String getStalemateMessage(Player activePlayer) {
		return this.stalemateMessage + " " + activePlayer.color + " wins";
	}
}

 package game;


 /**
  * Rules contains custom starting positions and custom move validation for each piece 
  */
public class Rules {
	public String stalemateMessage;

	public Rules() {

	}

	/**
	 * Creates a newly set up board
	 * @return The game board with pieces in starting positions
	 */
	public Board SetStartingPositions(Player white, Player black) {
		return null;
	}

	/**
	 * Returns the validity of a move
	 * @return:
	 * 0 - move is valid
	 * 1 - Move doesn't align with the rules for that piece types allowed movement
	 * 2 - Movement puts the user in check
	 * 3 - A kill is possible at another spot on the board but the user is moving to not kill
	*/
	public int ValidateMove(Move move, Board board)
	{
		return 1;
	}

	/**
	 * Allows rules to have control over what happens after a move
	 * @param activePlayer 			- reference to the player whos turn it is
	 * @param board 				- reference to the game board
	 * @param move					- reference to the move project
	 * @return MoveCompleteResult	- result of the last move, whether it ends the game and who wins
	 */
	public MoveCompleteResult ruleCompleteMove(Player activePlayer, Board board, Move move){
		return new MoveCompleteResult(false);
	}

	/**
	 * Allows rules to have control over what happens after a move
	 * @param board 				- reference to the game board
	 * @param move					- reference to the move project
	 * @return MoveCompleteResult	- result of the last move, whether it ends the game and who wins
	 */
	public MoveCompleteResult ruleCompleteMove(Board board, Move move){
		return new MoveCompleteResult(false);
	}

	/**
	 * Called after every move in classic and peasants revolt. Checks if the move a player just submitted puts them self into check
	 * @return false - move given puts the active player in check, true - the move is valid, it doesn't put the active player in check.
	 */
	private boolean validateBoardState(Move move, Board board) {
		return false;
	}

	/**
	 * Called when a pawn gets to the far side of the board. Gets the choice of piece from teh user and changes the piece accordingly.
	 */
	public void getPawnPromotionInput(Board board, Move move) {

	}

	/**
	 * Called before every move, checks for stalemate and ends the game accordingly.
	 */
	public boolean checkForStalemate(Player activePlayer, Board board) {
		return false;
	}

	/**
	 * Gives the stalemate message based on which player was put into stalemate.
	 * @param activePlayer - The player who's turn it currently is.
	 * @return true if the current player was just put into stalemate, false if they weren't
	 */
	public String getStalemateMessage(Player activePlayer) {
		return this.stalemateMessage;
	}

	public boolean CheckForCheck(Player activePlayer, Board board) {
		return false;
	}

}

package game;

/**
 * Extends Piece and uses a letter to represent the value
 */
public class Rook extends Piece {
	public Rook (Player owner) {
		super(owner);
	}

	/**
	 * returns the letter that represents the piece for the color they are on
	 */
	public String toString() {
		if (owner.color.equals("White")) {
			return "R";
		}
		return "r";

	}
}

package game;

/**
 * Extends Piece and uses a letter to represent the value
 */
public class Queen extends Piece {
	public Queen (Player owner) {
		super(owner);
	}

	/**
	 * returns the letter that represents the piece for the color they are on
	 */
	public String toString() {
		if (owner.color.equals("White")) {
			return "Q";
		}
		return "q";

	}
}

package game;

/**
 * Container that has a reference to the player's color and opponent
 */
public class Player {

	// "White" or "Black"
	public String color;
	public Player opponent = null;

	public String toString() {
		return color;
	}

}

package game;

/**
 * Generic Piece class that is extended by each individual game piece (Bishop, King, Knight, Pawn, Queen, Rook).
 * Contains a reference to the player and a variable for if it has moved.
 */
public class Piece {

	public Player owner = null;
	public Boolean hasMoved = false;

	public Piece(Player owner) {
		this.owner = owner;
	}


}

package game;
import java.io.*;

/**
 * 
 * This is the class that represents the game mode of Peasants Revolt. The class contains the rules for validating moves and checking boardstate (e.g. check, checkmate, etc.).
 *
 */
public class PeasantsRevoltChess extends Rules {

	public PeasantsRevoltChess() {
		super();

		this.stalemateMessage = "No valid moves available, game ends in a stalemate.";
	}

	/**
	 * 
	 */
	public Board SetStartingPositions(Player white, Player black) {

		Board board = new Board(8,8);

		board.AddPiece(new Pawn(white), 0, 1);
		board.AddPiece(new Pawn(white), 1, 1);
		board.AddPiece(new Pawn(white), 2, 1);
		board.AddPiece(new Pawn(white), 3, 1);
		board.AddPiece(new Pawn(white), 4, 1);
		board.AddPiece(new Pawn(white), 5, 1);
		board.AddPiece(new Pawn(white), 6, 1);
		board.AddPiece(new Pawn(white), 7, 1);
		board.AddPiece(new King(white), 4, 0);

		board.AddPiece(new Pawn(black), 4, 6);
		board.AddPiece(new Knight(black), 1, 7);
		board.AddPiece(new Knight(black), 2, 7);
		board.AddPiece(new King(black), 4, 7);
		board.AddPiece(new Knight(black), 5, 7);
		board.AddPiece(new Knight(black), 6, 7);

		return board;
	}

	/**
	 * This method verifies the move is valid for the specified piece.
	 * @param move The suggested move inputed by the player.
	 * @param board The board in its current state
	 * @return true if it's a valid move, false if invalid
	 */
	public boolean validatePawnMove(Move move, Board board)
	{
		// get direction it can go in
		int direction = -1;

		// white on top
		if(move.activePlayer.color.equals("White")) {
			direction = 1;
		}

		int yDifference = move.endPosition.yCord - move.startPosition.yCord;
		int xDifference = Math.abs(move.endPosition.xCord - move.startPosition.xCord);
		if(yDifference == direction) {
			// pawn is moving straight by 1 space
			if(xDifference == 0) {
				// if there is no piece ahead, it's valid
				if(move.endPosition.piece == null) {
					return true;
				}
				else {
					return false;
				}
			}
			// pawn is moving diagonal by 1 space
			else if(xDifference == 1) {
				// can't move diagonal without a piece
				if(move.endPosition.piece == null) {
					//checking if the piece adjacent to the pawn moving was a pawn and had done a double jump the last turn (checking condition for En Passant)
					if(board.tiles[move.endPosition.xCord][move.startPosition.yCord].piece != null){
						if(board.tiles[move.endPosition.xCord][move.startPosition.yCord].piece.getClass() == Pawn.class){
							Pawn p = (Pawn)board.tiles[move.endPosition.xCord][move.startPosition.yCord].piece;
							if(p.lastMoveJump == true && p.owner != move.activePlayer){
								board.tiles[move.endPosition.xCord][move.startPosition.yCord].piece = null;
								return true;
							}
						}
					}
					return false;
				}
				// can only move diagonal if the piece is owned by the other player
				else if(move.endPosition.piece.owner != move.activePlayer) {
					return true;
				}
			}
		}
		else if(move.piece.hasMoved == false && yDifference == direction * 2 && xDifference == 0) {
			// double jump for first move
			//checking that the final position is empty (does not contain a piece) and that the space in between is also empty.
			if(move.endPosition.piece == null && board.tiles[move.startPosition.xCord][move.startPosition.yCord + direction].piece == null) {
				return true;
			}
			else {
				return false;
			}
		}
		return false;
	}

	/**
	 * This method verifies the move is valid for the specified piece.
	 * @param move The suggested move inputed by the player.
	 * @param board The board in its current state
	 * @return true if it's a valid move, false if invalid
	 */
	public boolean validateQueenMove(Move move, Board board)
	{
		// check what the move way is, horizontal or diagonal
		int xDiff = Math.abs(move.startPosition.xCord - move.endPosition.xCord);
		int yDiff = Math.abs(move.startPosition.yCord - move.endPosition.yCord);

		// if it attacks the same team
		if(move.endPosition.piece != null && move.endPosition.piece.owner == move.activePlayer) {
			return false;
		}

		int xDir = 1;
		int yDir = 1;

		// moving left
		if(move.startPosition.xCord - move.endPosition.xCord > 0) {
			xDir = -1;
		}
		// moving up
		if(move.startPosition.yCord - move.endPosition.yCord > 0) {
			yDir = -1;
		}

		// diagonal movement
		if(xDiff == yDiff) {
			for(int i=1; i < yDiff; i++ ) {
				Tile t = board.tiles[move.startPosition.xCord + (xDir * i)][move.startPosition.yCord + (yDir * i)];
				if(t.piece != null) {
					return false;
				}
			}
			return true;
		}
		// horizontal movement
		else if(yDiff == 0) {
			for(int i=1; i < xDiff; i++ ) {
				Tile t = board.tiles[move.startPosition.xCord + (xDir * i)][move.startPosition.yCord];
				if(t.piece != null) {
					return false;
				}
			}
			return true;
		}
		// vertical movement
		else if(xDiff == 0) {
			for(int i=1; i < yDiff; i++ ) {
				Tile t = board.tiles[move.startPosition.xCord][move.startPosition.yCord + (yDir * i)];
				if(t.piece != null) {
					return false;
				}
			}
			return true;
		}

		return false;
	}

	/**
	 * This method verifies the move is valid for the specified piece.
	 * @param move The suggested move inputed by the player.
	 * @param board The board in its current state
	 * @return true if it's a valid move, false if invalid
	 */
	public boolean validateBishopMove(Move move, Board board)
	{
		// check what the move way is, horizontal or diagonal
		int xDiff = Math.abs(move.startPosition.xCord - move.endPosition.xCord);
		int yDiff = Math.abs(move.startPosition.yCord - move.endPosition.yCord);

		// if it attacks the same team
		if(move.endPosition.piece != null && move.endPosition.piece.owner == move.activePlayer) {
			return false;
		}

		// diagonal movement
		if(xDiff == yDiff) {
			int xDir = 1;
			int yDir = 1;

			// moving left
			if(move.startPosition.xCord - move.endPosition.xCord > 0) {
				xDir = -1;
			}
			// moving up
			if(move.startPosition.yCord - move.endPosition.yCord > 0) {
				yDir = -1;
			}

			for(int i=1; i < yDiff; i++ ) {
				Tile t = board.tiles[move.startPosition.xCord + (xDir * i)][move.startPosition.yCord + (yDir * i)];
				if(t.piece != null) {
					return false;
				}
			}
			return true;
		}

		return false;
	}

	/**
	 * This method verifies the move is valid for the specified piece.
	 * @param move The suggested move inputed by the player.
	 * @param board The board in its current state
	 * @return true if it's a valid move, false if invalid
	 */
	public boolean validateRookMove(Move move, Board board)
	{
		// check what the move way is, horizontal or diagonal
		int xDiff = Math.abs(move.startPosition.xCord - move.endPosition.xCord);
		int yDiff = Math.abs(move.startPosition.yCord - move.endPosition.yCord);

		// if it attacks the same team
		if(move.endPosition.piece != null && move.endPosition.piece.owner == move.activePlayer) {
			return false;
		}

		int xDir = 1;
		int yDir = 1;

		// moving left
		if(move.startPosition.xCord - move.endPosition.xCord > 0) {
			xDir = -1;
		}
		// moving up
		if(move.startPosition.yCord - move.endPosition.yCord > 0) {
			yDir = -1;
		}

		// vertical movement
		if(xDiff == 0) {
			for(int i=1; i < yDiff; i++ ) {

				Tile t = board.tiles[move.startPosition.xCord][move.startPosition.yCord + (yDir * i)];
				if(t.piece != null) {
					return false;
				}
			}
			return true;
		}
		// horizontal movement
		else if(yDiff == 0) {
			for(int i=1; i < xDiff; i++ ) {
				Tile t = board.tiles[move.startPosition.xCord + (xDir * i)][move.startPosition.yCord];
				if(t.piece != null) {
					return false;
				}
			}
			return true;
		}

		return false;
	}

	/**
	 * This method verifies the move is valid for the specified piece.
	 * @param move The suggested move inputed by the player.
	 * @param board The board in its current state
	 * @return true if it's a valid move, false if invalid
	 */
	public boolean validateKnightMove(Move move, Board board)
	{
		// if it attacks the same team
		if(move.endPosition.piece != null && move.endPosition.piece.owner == move.activePlayer) {
			return false;
		}

		// check what the move way is, horizontal or diagonal
		int xDiff = Math.abs(move.startPosition.xCord - move.endPosition.xCord);
		int yDiff = Math.abs(move.startPosition.yCord - move.endPosition.yCord);


		if(yDiff == 2 && xDiff == 1) {
			return true;
		}
		else if(yDiff == 1 && xDiff == 2) {
			return true;
		}

		return false;
	}

	/**
	 * This method verifies the move is valid for the specified piece.
	 * @param move The suggested move inputed by the player.
	 * @param board The board in its current state
	 * @return true if it's a valid move, false if invalid
	 */
	public boolean validateKingMove(Move move, Board board)
	{
		// if it attacks the same team
		if(move.endPosition.piece != null && move.endPosition.piece.owner == move.activePlayer) {
			return false;
		}

		// check what the move way is, horizontal or diagonal
		int xDiff = Math.abs(move.startPosition.xCord - move.endPosition.xCord);
		int yDiff = Math.abs(move.startPosition.yCord - move.endPosition.yCord);


		if(yDiff <= 1 && xDiff <= 1) {
			return true;
		}
		// the king is attempting to castle
		else if(xDiff == 2) {
			// can't castle if the king has moved
			if(move.piece.hasMoved == true) {
				return false;
			}
			int startXCord = move.startPosition.xCord;
			int startYCord = move.startPosition.yCord;

			// check right rook
			if(move.startPosition.xCord < move.endPosition.xCord) {
				// if a piece isn't there, if it has moved, or if it isn't a rook, it doesn't pass
				if(board.tiles[7][startYCord].piece == null || board.tiles[7][startYCord].piece.hasMoved == true || board.tiles[7][startYCord].piece.getClass() != Rook.class) {
					return false;
				}

				// if there are no pieces inbetween
				if(board.tiles[startXCord + 1][startYCord].piece == null && board.tiles[startXCord + 2][startYCord].piece == null) {
					board.tiles[startXCord + 1][startYCord].piece = board.tiles[7][startYCord].piece;
					board.tiles[7][startYCord].piece = null;
					return true;
				}
			}
			// check left rook
			else {
				// if a piece isn't there, if it has moved, or if it isn't a rook, it doesn't pass
				if(board.tiles[0][startYCord].piece == null || board.tiles[0][startYCord].piece.hasMoved == true || board.tiles[0][startYCord].piece.getClass() != Rook.class) {
					return false;
				}

				// if there are no pieces inbetween
				if(board.tiles[startXCord - 1][startYCord].piece == null && board.tiles[startXCord - 2][startYCord].piece == null && board.tiles[startXCord - 3][startYCord].piece == null) {
					board.tiles[startXCord - 1][startYCord].piece = board.tiles[0][startYCord].piece;
					board.tiles[0][startYCord].piece = null;
					return true;
				}
			}
		}

		return false;
	}

	/**
	 * This method verifies the move is valid for all pieces. It does this by calling the specific method for each piece, as well as calling the method to check for boardstate.
	 * @param move The suggested move inputed by the player.
	 * @param board The board in its current state
	 * @return true if it's a valid move, false if invalid
	 */
	public int ValidateMove(Move move, Board board)
	{
		int isValid = 1;

		if(move.piece.getClass() == Pawn.class) {
			isValid = (validatePawnMove(move, board) == true) ? 0 : 1;
		}
		else if(move.piece.getClass() == Bishop.class) {
			isValid = (validateBishopMove(move, board) == true) ? 0 : 1;
		}
		else if(move.piece.getClass() == Queen.class) {
			isValid = (validateQueenMove(move, board) == true) ? 0 : 1;
		}
		else if(move.piece.getClass() == Rook.class) {
			isValid = (validateRookMove(move, board) == true) ? 0 : 1;
		}
		else if(move.piece.getClass() == Knight.class) {
			isValid = (validateKnightMove(move, board) == true) ? 0 : 1;
		}
		else if(move.piece.getClass() == King.class) {
			isValid = (validateKingMove(move, board) == true) ? 0 : 1;
		}

		if(isValid == 1) {
			View.setErrorMessage("Illegally inputted move, piece can't move in the specified manner.");
		}

		isValid = validateBoardState(move, board) ? isValid : 2;
		if(isValid == 2) {
			View.setErrorMessage("Move puts the King in check");
		}

		return isValid;
	}

	/**
	 * This method is used to check if the player is going to be placed into check by making the move.
	 * @param activePlayer - what player you want to check if has their opponent in check
	 * @param board - the board you want to check for check on.
	 * @return true if the active player is in check, false if they are not.
	 */
	public boolean CheckForCheck(Player activePlayer, Board board) {

		//locate the friendly and enemy kings
		Tile kingPosition = null;
		Tile enemyKing = null;
		for (Tile tile: board.listOfTiles) {
			if (tile.piece != null) {
				if (tile.piece.getClass() == King.class && tile.piece.owner == activePlayer) {
					kingPosition = tile;
				}
				else if (tile.piece.getClass() == King.class && tile.piece.owner != activePlayer) {
					enemyKing = tile;
				}
			}
		}
		//if (kingPosition == null) { return false; }
		if (enemyKing == null) { return false; }

		//iterate through the active players pieces and tell them to try and kill the friendly king
		//if they can (legally) then the active player is in check
		for (Tile tile : board.listOfTiles) {
			if (tile.piece != null) {
				if (tile.piece.owner == activePlayer) {
					//try to kill the king
					Move regicide = new Move();
					regicide.activePlayer = tile.piece.owner;
					regicide.startPosition = tile;
					regicide.endPosition = enemyKing;
					regicide.piece = tile.piece;

					boolean isValid = false;
					if(regicide.piece.getClass() == Pawn.class) {
						isValid = validatePawnMove(regicide, board);
					}
					else if(regicide.piece.getClass() == Bishop.class) {
						isValid = validateBishopMove(regicide, board);
					}
					else if(regicide.piece.getClass() == Queen.class) {
						isValid = validateQueenMove(regicide, board);
					}
					else if(regicide.piece.getClass() == Rook.class) {
						isValid = validateRookMove(regicide, board);
					}
					else if(regicide.piece.getClass() == Knight.class) {
						isValid = validateKnightMove(regicide, board);
					}
					else if(regicide.piece.getClass() == King.class) {
						isValid = validateKingMove(regicide, board);
					}
					if (isValid) {
						return true;
					}
				}
			}
		}

		return false;
	}

	/**
	 * Checks if a player put themselves into check
	 * @param move The suggested move inputed by the player
	 * @param board The board in its current state
	 * @return false if the move put the active player in check, true if they didn't
	 */
	private boolean validateBoardState(Move move, Board board) {

		//create deep copies of move and board
		Board newBoard = new Board(board);
		Move newMove = new Move(move);

		newMove.ConvertToDifferentBoard(newBoard);

		//make the suggested move on the fake board
		newMove.startPosition.piece = null;
		newMove.endPosition.piece = newMove.piece;

		return !CheckForCheck(newMove.activePlayer.opponent, newBoard);

	}

	/**
	 * Sets the boolean value for last move jump to false every move that occurs.
	 * @param activePlayer The current player.
	 * @param board The current board.
	 */
	public void setLastMoveJump(Player activePlayer, Board board){
		for(int y = 0; y < board.height; y++) {
			for(int x = 0; x < board.width; x++) {
				if (board.tiles[x][y].piece != null) {
					if(board.tiles[x][y].piece.getClass() == Pawn.class && board.tiles[x][y].piece.owner == activePlayer){
						Pawn p = (Pawn)board.tiles[x][y].piece;
						p.lastMoveJump = false;
					}
				}
			}
		}
	}


	/**
	 * Checks for special conditions, such as checkmate.
	 * @param activePlayer the current player.
	 * @param board The current board.
	 * @param move The move that just occurred.
	 * @return MoveCompleteResult The result of the move occurring.
	 */
	public MoveCompleteResult ruleCompleteMove(Player activePlayer, Board board, Move move){
		setLastMoveJump(activePlayer, board);
		if(move.piece.getClass() == Pawn.class && Math.abs(move.startPosition.yCord - move.endPosition.yCord) == 2) {
			Pawn p = (Pawn)move.piece;
			p.lastMoveJump = true;
		}else if(move.piece.getClass() == Pawn.class && move.endPosition.yCord == 7 && move.piece.owner.color.equals("White")) {
			getPawnPromotionInput(board, move);
		}else if(move.piece.getClass() == Pawn.class && move.endPosition.yCord == 0 && move.piece.owner.color.equals("Black")) {
			getPawnPromotionInput(board, move);
		}

		if (CheckForCheck(activePlayer, board)) {

			boolean checkmate = true;
			Board newBoard = new Board(board);
			Move savingMove = null;

			for (Tile tile: newBoard.listOfTiles) {
				if (checkmate && tile.piece != null && tile.piece.owner == activePlayer.opponent) {

					for (Tile endPosition: newBoard.listOfTiles) {
						savingMove = new Move();
						savingMove.activePlayer = activePlayer.opponent;
						savingMove.piece = tile.piece;
						savingMove.startPosition = tile;
						savingMove.endPosition = endPosition;
						//System.out.println("New saving move attempt: " + savingMove.piece + " " + savingMove.startPosition.xCord + "," + savingMove.startPosition.yCord + "-" + savingMove.endPosition.xCord + "," + savingMove.endPosition.yCord);

						boolean isValid = false;
						if(savingMove.piece.getClass() == Pawn.class) {
							isValid = validatePawnMove(savingMove, newBoard);
						}
						else if(savingMove.piece.getClass() == Bishop.class) {
							isValid = validateBishopMove(savingMove, newBoard);
						}
						else if(savingMove.piece.getClass() == Queen.class) {
							isValid = validateQueenMove(savingMove, newBoard);
						}
						else if(savingMove.piece.getClass() == Rook.class) {
							isValid = validateRookMove(savingMove, newBoard);
						}
						else if(savingMove.piece.getClass() == Knight.class) {
							isValid = validateKnightMove(savingMove, newBoard);
						}
						else if(savingMove.piece.getClass() == King.class) {
							isValid = validateKingMove(savingMove, newBoard);
						}
						//System.out.println("valid? " + isValid);
						if (isValid) {
							Board tempBoard = new Board(newBoard);
							savingMove.ConvertToDifferentBoard(tempBoard);

							//do the move
							savingMove.startPosition.piece = null;
							savingMove.endPosition.piece = savingMove.piece;

							//System.out.println("but does it get us out of check? " + !CheckForCheck(activePlayer, tempBoard));
							if (!CheckForCheck(activePlayer, tempBoard)) {
								checkmate = false;
							}
						}
					}
				}
			}

			if (checkmate) {
				System.out.println(activePlayer + " has put " + activePlayer.opponent + " into checkmate!");
				MoveCompleteResult moveResult = new MoveCompleteResult(true);
				moveResult.winner = activePlayer;
				return moveResult;
			}

			System.out.println(activePlayer + " has put " + activePlayer.opponent + " into check.");
		}
		return new MoveCompleteResult(false);
	}

	/**
	 * Gets input for doing the Pawn Promotion
	 */
	public void getPawnPromotionInput(Board board, Move move){
		System.out.println("Please enter a valid input for Pawn Promotion. Choose between Q (Queen), R (Rook), N (Knight), and B (Bishop).");
		BufferedReader br = new BufferedReader(new InputStreamReader(System.in));
		String input = null;
		while (input == null) {
			try {
				input = br.readLine();
			} catch (IOException e) {
				input = null;
				System.out.println("Input Error: Unable to read input.");
			}
			if (input != null) {
				try {
					if(input.equalsIgnoreCase("q") || input.equalsIgnoreCase("queen")) {
						board.tiles[move.endPosition.xCord][move.endPosition.yCord].addPiece(new Queen(move.activePlayer));
					}
					else if(input.equalsIgnoreCase("r") || input.equalsIgnoreCase("rook")) {
						board.tiles[move.endPosition.xCord][move.endPosition.yCord].addPiece(new Rook(move.activePlayer));
					}
					else if(input.equalsIgnoreCase("n") || input.equalsIgnoreCase("knight")) {
						board.tiles[move.endPosition.xCord][move.endPosition.yCord].addPiece(new Knight(move.activePlayer));
					}
					else if(input.equalsIgnoreCase("b") || input.equalsIgnoreCase("bishop")) {
						board.tiles[move.endPosition.xCord][move.endPosition.yCord].addPiece(new Bishop(move.activePlayer));
					}
					else{
						input = null;
						System.out.println("Please choose a valid input between Q (Queen), R (Rook), N (Knight), and B (Bishop).");
					}
				}
				catch (Exception e)
				{
					input = null;
					System.out.println("Please choose a valid input between Q (Queen), R (Rook), N (Knight), and B (Bishop).");
				}
			}
		}
	}

	/**
	 * Called before every move, checks for stalemate and ends the game accordingly.
	 */
	public boolean checkForStalemate(Player activePlayer, Board board) {
		//Check all pieces for a valid move, to start with the player doesn't need to move his King.
		Tile kingTile = null;

		for (Tile tile : board.listOfTiles) {
			if (tile.piece != null) {
				if (tile.piece.owner == activePlayer) {
					Move exMove = new Move();
					exMove.activePlayer = tile.piece.owner;
					exMove.startPosition = tile;
					exMove.piece = tile.piece;
					if(tile.piece.getClass() == King.class) {
						kingTile = tile;
					}
					for (Tile endTile : board.listOfTiles) {
						exMove.endPosition = endTile;
						boolean isValid = false;
						if(exMove.piece.getClass() == Pawn.class) {
							isValid = validatePawnMove(exMove, board);
						}
						else if(exMove.piece.getClass() == Bishop.class) {
							isValid = validateBishopMove(exMove, board);
						}
						else if(exMove.piece.getClass() == Queen.class) {
							isValid = validateQueenMove(exMove, board);
						}
						else if(exMove.piece.getClass() == Rook.class) {
							isValid = validateRookMove(exMove, board);
						}
						else if(exMove.piece.getClass() == Knight.class) {
							isValid = validateKnightMove(exMove, board);
						}
						if (isValid) {
							return false;
						}
					}
				}
			}
		}

		//Has no valid moves other than potentially the king.
		if(kingTile == null) { //player has no king, can't be in stalemate with no king.
			return false;
		}

		//Loop through all the potential moved for the king, and check if the player is in check for each of them.
		Move exMove = new Move();
		exMove.activePlayer = activePlayer;
		exMove.startPosition = kingTile;
		exMove.piece = kingTile.piece;

		for (Tile endTile : board.listOfTiles) {
			exMove.endPosition = endTile;
			if(!validateKingMove(exMove, board)) {
				continue;
			}
			//is a valid move for the king. Perform the move on a fake board and check for check.
			Board newBoard = new Board(board);	
			newBoard.tiles[endTile.xCord][endTile.yCord].piece = new King(activePlayer);
			newBoard.tiles[kingTile.xCord][kingTile.yCord].piece = null;

			if(!CheckForCheck(activePlayer.opponent, newBoard)) { //opponent doesn't have them in check after the move
				return false;
			}
		}

		return true;
	}

	/**
	 * Gives the stalemate message based on which player was put into stalemate.
	 * @param activePlayer - The player who's turn it currently is.
	 * @return true if the current player was just put into stalemate, false if they weren't
	 */
	public String getStalemateMessage(Player activePlayer) {
		return this.stalemateMessage;
	}
}

package game;

/**
 * Extends Piece and uses a letter to represent the value
 */
public class Pawn extends Piece {
	public Boolean lastMoveJump = false;
	public Pawn (Player owner) {
		super(owner);
	}

	/**
	 * returns the letter that represents the piece for the color they are on
	 */
	public String toString() {
		if (owner.color.equals("White")) {
			return "P";
		}
		return "p";

	}
}

package game;

/**
 * Container class for the result of a game state. Specifying if the game is ended or not. If the game is ended, 
 * and no winner is provided it is assumed to be a stalemate.
 *
 */
public class MoveCompleteResult {
	public boolean gameOver;
	public Player winner;

	public MoveCompleteResult(boolean endGame)
	{
		gameOver = endGame;
	}
}

package game;

/**
 *  Class to hold a move a piece can make, their start/end positions, and their player
 */
public class Move {

	public Player activePlayer = null;
	public Piece piece = null;
	public Tile endPosition = null;
	public Tile startPosition = null;
	public String text = "";

	public Move() {}

	public Move(Move move) {
		this.activePlayer = move.activePlayer;
		this.startPosition = move.startPosition;
		this.endPosition = move.endPosition;
		this.piece = move.piece;
	}

	public Move(Player activePlayer, Tile startTile, Tile endTile) {
		this.activePlayer = activePlayer;
		this.startPosition = startTile;
		this.piece = startTile.piece;
		this.endPosition = endTile;
	}

	public Move(Player activePlayer, String move, Board board) {
		this.text = move;
		this.activePlayer = activePlayer;

		String start = move.substring(0, move.indexOf('-')).toLowerCase();
		String end = move.substring(move.indexOf('-')+1).toLowerCase();

		int startX = -1;
		int startY = Integer.valueOf(start.substring(1)); //Character.getNumericValue(start.charAt(1));

		startX = (start.charAt(0) == 'a') ? 0 : startX;
		startX = (start.charAt(0) == 'b') ? 1 : startX;
		startX = (start.charAt(0) == 'c') ? 2 : startX;
		startX = (start.charAt(0) == 'd') ? 3 : startX;
		startX = (start.charAt(0) == 'e') ? 4 : startX;
		startX = (start.charAt(0) == 'f') ? 5 : startX;
		startX = (start.charAt(0) == 'g') ? 6 : startX;
		startX = (start.charAt(0) == 'h') ? 7 : startX;
		startX = (start.charAt(0) == 'i') ? 8 : startX;
		startX = (start.charAt(0) == 'j') ? 9 : startX;

		if(startX != -1 && startY < board.height && startY > -1) {
			Tile tile = board.tiles[startX][startY];

			piece = tile.piece;
			startPosition = tile;
			tile.xCord = startX;
			tile.yCord = startY;
		}

		int endX = -1;
		int endY = Integer.valueOf(end.substring(1));;

		endX = (end.charAt(0) == 'a') ? 0 : endX;
		endX = (end.charAt(0) == 'b') ? 1 : endX;
		endX = (end.charAt(0) == 'c') ? 2 : endX;
		endX = (end.charAt(0) == 'd') ? 3 : endX;
		endX = (end.charAt(0) == 'e') ? 4 : endX;
		endX = (end.charAt(0) == 'f') ? 5 : endX;
		endX = (end.charAt(0) == 'g') ? 6 : endX;
		endX = (end.charAt(0) == 'h') ? 7 : endX;
		endX = (end.charAt(0) == 'i') ? 8 : endX;
		endX = (end.charAt(0) == 'j') ? 9 : endX;

		if(endX != -1 && endY < board.height && endY > -1) {
			endPosition = board.tiles[endX][endY];
			endPosition.xCord = endX;
			endPosition.yCord = endY;
		}
	}

	/**
	 * Determines if a move is valid. If it is invalid it sets an error message for the view to display later.
	 * @return True if valid. False if invalid.
	 */
	public boolean IsValid()
	{
		if (piece != null && startPosition != null && endPosition != null && activePlayer != null && piece.owner == activePlayer)
			return true;

		if(endPosition == null) { View.setErrorMessage("Move not possible, tile specified for end position doesn't exist."); }
		if(startPosition == null) { View.setErrorMessage("Move not possible, tile specified for start position doesn't exist."); }
		if(activePlayer == null) { View.setErrorMessage("System error, no active player."); }
		if(piece == null) { View.setErrorMessage("Move not possible, piece doesn't exist on specified tile."); }
		if(piece != null && piece.owner != activePlayer) { View.setErrorMessage("Move not possible, active player doesn't own the piece specified to move."); }

		return false;
	}

	public String toString() {
		return "== Move == "
				+ "\n Start Position => " + this.startPosition.toString() 
				+ "\n End Position => " + this.endPosition.toString();

	}

	public void ConvertToDifferentBoard(Board newBoard) {
		for (Tile tile: newBoard.listOfTiles) {
			if (this.startPosition.xCord == tile.xCord && 
					this.startPosition.yCord == tile.yCord) {
				this.startPosition = tile;
			}
			else if (this.endPosition.xCord == tile.xCord && 
					this.endPosition.yCord == tile.yCord) {
				this.endPosition = tile;
			}
		}
	}

}

